mapper.select:
  		select q.qna_seqno as qnaSeqno, q.qna_content as qnaContent, q.mem_id as memId, q.qna_date as qnaDate
		from qna q, pro p
		where q.pro_seqno = p.pro_seqno and p.pro_seqno = #{seqno}

		select r.review_seqno as reviewSeqno, r.review_content as reviewContent, r.mem_id as memId, r.pro_seqno as proSeqno
      from review r, pro p
      where p.pro_seqno = r.pro_seqno and p.pro_seqno = #{seqno}
